mapper.insert:
INSERT INTO ATTACHMENT (FILE_NO,ORIGIN_NAME,CHANGE_NAME,REF_FNO,FILE_TYPE,IMG_LEVEL) SELECT SEQ_ATTNO.NEXTVAL,C.* FROM(
SELECT #{attachment.originName}, #{attachment.changeName}, #{attachment.refFno}, 'B', #{attachment.imgLevel} FROM DUAL
) C

mapper.update:
UPDATE BOARD
SET COUNT = COUNT+1
WHERE BOARD_NO =#{boardNo}

UPDATE BOARD
SET BOARD_TITLE = #{boardTitle},
BOARD_CONTENT = #{boardContent}
WHERE BOARD_NO = #{boardNo}

UPDATE BOARD
SET STATUS='N'
WHERE BOARD_NO=#{boardNo}

UPDATE REPLY
SET STATUS='N'
WHERE BOARD_NO = #{boardNo}

UPDATE REPLY
SET REPLY_CONTENT = #{replyContent}
WHERE REPLY_NO = #{replyNo}

UPDATE REPLY
SET STATUS ='N'
WHERE REPLY_NO=#{replyNo}
